  border-radius: 8px; /* Rounded corners for smooth appearance */
  animation: shimmer 1.5s infinite linear; /* Applies shimmer animation */
}

/* Shimmer animation */
@keyframes shimmer {
  0% { background-color: #e0e0e0; }  /* Darker grey at start */
  50% { background-color: #f8f8f8; } /* Lighter grey in the middle */
  100% { background-color: #e0e0e0; } /* Back to darker grey */
}

