  background-color: #fff; /* White background */
  border-radius: 12px; /* Rounded corners for a softer look */
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1); /* Subtle shadow for depth */
  font-family: Arial, sans-serif;
}

