.restro-name {
  font-size: 28px;
  font-weight: bold;
  text-align: center;
  margin-bottom: 10px;
  color: #333; /* Dark color for readability */
}

/* Cuisines */
.restro-cuisines {
  font-size: 16px;
  text-align: center;
  color: #666; /* Slightly lighter text */
  margin-bottom: 10px;
}

/* Cost for Two */
.restro-price {
  font-size: 18px;
  font-weight: bold;
  text-align: center;
  color: #27ae60; /* Green color for price */
  margin-bottom: 20px;
}

/* Menu List */
.restro-menu {
  list-style: none;
  padding: 0;
}

