.restro-menu li {
  font-size: 16px;
  background-color: #f9f9f9; /* Light background for items */
  padding: 10px;
  margin-bottom: 8px;
  border-radius: 8px; /* Rounded corners for list items */
  display: flex;
  justify-content: space-between;
  align-items: center;
  color: #444;
  border: 1px solid #ddd; /* Light border */
}

/* Price Styling */
.restro-menu li span {
  font-weight: bold;
  color: #d35400; /* Orange price */
}

/* Hover Effect */
.restro-menu li:hover {
  background-color: #eee; /* Slightly darker on hover */
  cursor: pointer;
}

/* Responsive Design */
@media (max-width: 768px) {
  .menu {
    width: 90%; /* Adjust width for smaller screens */
    padding: 15px;
  }

  .restro-name {
    font-size: 24px;
  }

  .restro-cuisines {
    font-size: 14px;
  }

  .restro-menu li {
    font-size: 14px;
    padding: 8px;
  }
}

.error-msg {
  font-size: 3rem; /* Large and bold */
  font-weight: bold;
  color: #fff; /* White text for contrast */
  text-shadow: 2px 2px 5px rgba(0, 0, 0, 0.3); /* Adds depth */
  background: linear-gradient(to right, #ff4d4d, #d91e18); /* Darker red gradient */
  padding: 15px 25px;
  border-radius: 10px;
  display: inline-block;
  margin-bottom: 20px;
  animation: fadeIn 1.2s ease-in-out;
}

/* Smooth fade-in effect */
@keyframes fadeIn {   
  from {
      opacity: 0;
      transform: translateY(-10px);
  }
  to {
      opacity: 1;
      transform: translateY(0);
  }
}

.user-card {
  padding: 10px;
  border: 1px solid black;
}

